mapper.insert:

    SELECT ADMIN_SEQ.NEXTVAL
    FROM DUAL

INSERT INTO ADMIN_T (
      ADMIN_NO
    , ADMIN_ID
    , ADMIN_PW
) VALUES (
      #{adminNo}
    , #{adminId}
    , #{adminPw})

mapper.update:
UPDATE ADMIN_T A
   SET A.REFRESH_TOKEN = #{refreshToken}
 WHERE 1 = 1
   AND A.ADMIN_NO = #{adminNo}
