mapper.select:
SELECT COUNT(*)
  FROM TIME_OFF_TEMP_T
 WHERE 1=1
   AND EMP_NO = #{empNo}
   AND TIME_OFF_CT_NO = #{timeOffCtNo}

mapper.delete:
DELETE
  FROM TIME_OFF_TEMP_T
 WHERE 1=1
   AND EMP_NO = #{empNo}
   AND TIME_OFF_CT_NO = #{timeOffCtNo}

mapper.insert:
INSERT INTO TIME_OFF_TEMP_T (
            TIME_OFF_TEMP_NO
          , TIME_OFF_CT_NO
          , TIME_OFF_START_DT
          , TIME_OFF_END_DT
          , TIME_OFF_PERIOD
          , TIME_OFF_CONTENT
          , EMP_NO
   ) VALUES (
            TIME_OFF_TEMP_SEQ.NEXTVAL
          , #{timeOffCtNo}
          , #{timeOffStartDt}
          , #{timeOffEndDt}
          , #{timeOffPeriod}
          , #{timeOffContent}
          , #{empNo})
