mapper.select:
SELECT COUNT(*)
  FROM TIME_OFF_TEMP_T
 WHERE 1=1
   AND EMP_NO = #{empNo}
   AND TIME_OFF_CT_NO = #{timeOffCtNo}

mapper.delete:
DELETE
  FROM TIME_OFF_TEMP_T
 WHERE 1=1
   AND EMP_NO = #{empNo}
   AND TIME_OFF_CT_NO = #{timeOffCtNo}

mapper.insert:
INSERT INTO TIME_OFF_TEMP_T (
            TIME_OFF_TEMP_NO
          , TIME_OFF_CT_NO
          , TIME_OFF_START_DT
          , TIME_OFF_END_DT
          , TIME_OFF_PERIOD
          , TIME_OFF_CONTENT
          , EMP_NO
   ) VALUES (
            TIME_OFF_TEMP_SEQ.NEXTVAL
          , #{timeOffCtNo}
          , #{timeOffStartDt}
          , #{timeOffEndDt}
          , #{timeOffPeriod}
          , #{timeOffContent}
          , #{empNo})


SELECT APPROVAL_SEQ.NEXTVAL
  FROM DUAL

INSERT INTO APPROVAL_T (
            APPR_NO
          , EMP_NO
          , CREATOR
   ) VALUES (
            #{apprNo}
          , #{empNo}
          , #{empNo})

INSERT INTO APPR_LINE_T ( APPR_LINE_NO , APPR_LINE_ORDER , APPR_NO , EMP_NO , CREATOR ) SELECT APPR_LINE_SEQ.NEXTVAL AS APPR_LINE_NO , TEMP_T.* FROM (
SELECT #{apprLineDto.apprLineOrder} AS APPR_LINE_ORDER , #{apprLineDto.apprNo} AS APPR_NO , #{apprLineDto.empNo} AS EMP_NO , #{apprLineDto.creator} AS CREATOR FROM DUAL
) TEMP_T

mapper.select:
SELECT COUNT(*) FROM APPROVAL_T A INNER JOIN TIME_OFF_T T ON A.APPR_NO = T.APPR_NO INNER JOIN EMPLOYEE_T E ON A.EMP_NO = E.EMP_NO
WHERE 1=1 AND E.EMP_NO = #{empNo}
RIGHT OUTER JOIN APPR_LINE_T L ON A.APPR_NO = L.APPR_NO WHERE 1=1 AND L.EMP_NO = #{empNo} AND ( L.APPR_LINE_ORDER = 0 OR ( SELECT 'temp' FROM APPR_LINE_T AL WHERE 1=1 AND AL.APPR_NO = A.APPR_NO AND AL.APPR_LINE_ORDER = 0 AND AL.APPR_LINE_STAT = 1 ) = 'temp')
AND T.TIME_OFF_CT_NO = #{timeOffCtNo}
AND A.APPR_STAT = #{apprStat}
AND E.EMP_NAME LIKE '%' || #{empName} || '%'

SELECT A.APPR_NO, A.APPR_STAT , TO_CHAR(A.APPR_DT, 'YYYY-MM-DD') AS APPR_DT , T.TIME_OFF_CT_NO , E.EMP_NO, E.EMP_NAME
, L.APPR_LINE_NO
FROM APPROVAL_T A INNER JOIN TIME_OFF_T T ON A.APPR_NO = T.APPR_NO INNER JOIN EMPLOYEE_T E ON A.EMP_NO = E.EMP_NO
WHERE 1=1 AND E.EMP_NO = #{empNo}
RIGHT OUTER JOIN APPR_LINE_T L ON A.APPR_NO = L.APPR_NO WHERE 1=1 AND L.EMP_NO = #{empNo} AND ( L.APPR_LINE_ORDER = 0 OR ( SELECT 'temp' FROM APPR_LINE_T AL WHERE 1=1 AND AL.APPR_NO = A.APPR_NO AND AL.APPR_LINE_ORDER = 0 AND AL.APPR_LINE_STAT = 1 ) = 'temp')
AND T.TIME_OFF_CT_NO = #{timeOffCtNo}
AND A.APPR_STAT = #{apprStat}
AND E.EMP_NAME LIKE '%' || #{empName} || '%'
ORDER BY A.APPR_DT
ASC DESC

mapper.update:
UPDATE APPR_LINE_T
   SET APPR_LINE_STAT = #{apprLineStat}
     , 
           APPR_SIGN = #{data}


           APPR_LINE_REASON = #{data}

 WHERE 1=1
   AND APPR_NO = #{apprNo}
   AND EMP_NO = #{empNo}
