mapper.insert:

    SELECT BOARD_SEQ.NEXTVAL FROM DUAL

INSERT INTO BOARD_T (
BOARD_NO
, BOARD_CT_NO
, BOARD_TITLE
, BOARD_CONTENT
, EMP_NO
, CREATE_DT
, CREATOR
) VALUES (
  #{boardNo}
, #{boardCtNo}
, #{boardTitle}
, #{boardContent}
, #{empNo}
, CURRENT_TIMESTAMP
, #{empNo}
)

mapper.select:
SELECT BOARD_FILE_NO, BOARD_PATH, BOARD_RENAME, BOARD_ORIGIN
FROM BOARD_FILE_T
WHERE BOARD_NO = #{boardNo}

SELECT BOARD_FILE_NO, BOARD_PATH, BOARD_RENAME, BOARD_ORIGIN
FROM BOARD_FILE_T
WHERE BOARD_FILE_NO = #{boardFileNo}

mapper.update:
UPDATE BOARD_T B
SET B.BOARD_TITLE = #{boardTitle}
  , B.BOARD_CONTENT = #{boardContent}
  , B.BOARD_CT_NO = #{boardCtNo}
  , B.MODIFY_DT = CURRENT_DATE
  , B.MODIFIER = #{modifier}
WHERE B.BOARD_NO = #{boardNo}

UPDATE BOARD_T B
  SET B.BOARD_STAT = 1,
      B.MODIFY_DT = CURRENT_DATE,
      B.MODIFIER = #{modifier}
 WHERE 1 = 1
   AND B.BOARD_NO = #{boardNo}

mapper.select:
SELECT COUNT(1)
  FROM BOARD_LIKE_T BL
 WHERE 1 = 1
   AND BL.BOARD_NO = #{boardNo}
   AND BL.EMP_NO = #{empNo}

mapper.update:
UPDATE BOARD_T B
   SET B.BOARD_LIKE_COUNT = #{boardLikeCount}
 WHERE BOARD_NO = #{boardNo}

UPDATE BOARD_T B
SET B.BOARD_HIT = B.BOARD_HIT + 1
WHERE BOARD_NO = #{boardNo}
