mapper.insert:

    SELECT BOARD_SEQ.NEXTVAL
    FROM DUAL

INSERT INTO BOARD_T (
BOARD_NO
, BOARD_CT_NO
, BOARD_TITLE
, BOARD_CONTENT
, EMP_NO
, CREATE_DT
, MODIFY_DT
) VALUES (
, #{boardNo}
, #{boardCtNo}
, #{boardTitle}
, #{boardContent}
, #{empNo}
, CURRENT_TIMESTAMP
, CURRENT_TIMESTAMP
)

INSERT INTO BOARD_FIEL_T (
                           BOARD_FILE_NO
                         , BOARD_PATH
                         , BOARD_RENAME
                         , BOARD_ORIGIN
                         , BOARD_NO
) VALUES (
           BOARD_FILE_SEQ.NEXTVAL
         , #{boardPath}
         , #{boardRename}
         , #{boardOrigin}
         , #{boardNo}
         )
