mapper.insert:

    SELECT COMMENT_SEQ.NEXTVAL FROM DUAL

INSERT INTO COMMENT_T (
      CMT_NO
    , CMT_CONTENT
    , CMT_CREATE_DT
    , CREATE_DT
    , CREATOR
    , BOARD_NO
    , PARENT_CMT_NO
    ) VALUES (
          #{cmtNo}
        , #{cmtContent}
        , CURRENT_DATE
        , CURRENT_DATE
        , #{creator}
        , #{boardNo}
        , #{parentCmtNo, jdbcType=INTEGER}
)

mapper.update:
UPDATE COMMENT_T C
SET C.CMT_CONTENT = #{cmtContent},
    C.MODIFY_DT = CURRENT_DATE,
    C.MODIFIER = #{modifier}
WHERE C.CMT_NO = #{cmtNo}
