mapper.insert:

      SELECT FRANCHISE_SEQ.NEXTVAL
      FROM DUAL

  INSERT INTO FRANCHISE_T (
        FRN_NO
      , FRN_NAME
      , FRN_OWNER
      , FRN_TEL
      , FRN_ZIP
      , FRN_STREET
      , FRN_DETAIL
      , FRN_START_TIME
      , FRN_END_TIME
      , CREATOR
      , CREATE_DT
      , FRN_JOIN_DT
) VALUES (
      #{frnNo}
    , #{frnName}
    , #{frnOwner}
    , #{frnTel}
    , #{frnZip}
    , #{frnStreet}
    , #{frnDetail}
    , #{frnStartTime}
    , #{frnEndTime}
    , 'admin'
    , CURRENT_DATE
    , CURRENT_DATE
  )

mapper.update:
UPDATE FRANCHISE_T F
SET F.FRN_TEL = #{frnTel},
    F.MODIFY_DT = CURRENT_DATE,
    F.MODIFIER = #{modifier}
WHERE 1 = 1
  AND F.FRN_NO = #{frnNo}

UPDATE FRANCHISE_T F
SET F.FRN_OWNER = #{frnOwner},
    F.MODIFY_DT = CURRENT_DATE,
    F.MODIFIER = ${modifier}
WHERE 1 = 1
  AND F.FRN_NO = ${frnNo}

UPDATE FRANCHISE_T F
F.FRN_START_TIME = #{frnStartTime},
F.FRN_END_TIME = #{frnEndTime},
F.MODIFY_DT = CURRENT_DATE, F.MODIFIER = ${modifier}
WHERE 1 = 1 AND F.FRN_NO = ${frnNo}
