mapper.insert:

    SELECT EMPLOYEE_SEQ.NEXTVAL
    FROM DUAL

INSERT INTO EMPLOYEE_T (
      EMP_NO
    , EMP_ID
    , EMP_PW
    , EMP_NAME
    , EMP_EMAIL
    , EMP_PHONE
    , EMP_PROFILE
    , POS_NO
    , FRN_NO
) VALUES (
      #{empNo}
    , #{empId}
    , #{empPw}
    , #{empName}
    , #{empEmail}
    , #{empPhone}
    , #{empProfile}
    , #{posNo}
    , #{frnNo}
)

mapper.select:
SELECT E.EMP_NO, E.EMP_ID, E.EMP_NAME, E.EMP_PHONE , E.EMP_STAT, F.FRN_NAME, P.POS_NAME FROM EMPLOYEE_T E LEFT JOIN FRANCHISE_T F ON E.FRN_NO = F.FRN_NO INNER JOIN POSITION_T P ON E.POS_NO = P.POS_NO WHERE 1 = 1 AND E.FRN_NO = #{frnNo} AND E.POS_NO != 0
AND E.POS_NO = #{posNo}
AND E.EMP_STAT = #{empStat}
ORDER BY E.EMP_NO

mapper.delete:
DELETE
  FROM EMPLOYEE_T E
 WHERE EMP_NO = #{empNo}
