mapper.select:
SELECT ROW_NUMBER() OVER( ORDER BY
I.IS_COUNT S.STOCK_NAME
DESC ASC
) RN , S.STOCK_NO, S.STOCK_IMG, S.STOCK_NAME , C.STOCK_CT_NAME , I.IS_COUNT FROM STOCK_T S JOIN STOCK_CATEGORY_T C ON S.STOCK_CT_NO = C.STOCK_CT_NO JOIN INVENTORY_STAT_T I ON S.STOCK_NO = I.STOCK_NO WHERE 1=1 AND I.FRN_NO = #{frnNo}
AND S.STOCK_CT_NO = #{stockCtNo}
