mapper.select:
SELECT COUNT(*) FROM STOCK_T S INNER JOIN STOCK_CATEGORY_T C ON S.STOCK_CT_NO = C.STOCK_CT_NO INNER JOIN INVENTORY_STAT_T I ON S.STOCK_NO = I.STOCK_NO WHERE 1=1 AND I.FRN_NO = #{frnNo}
AND S.STOCK_CT_NO = #{stockCtNo}
AND S.STOCK_NAME LIKE '%' || #{stockNameSearch} || '%'

SELECT ROW_NUMBER() OVER( ORDER BY
I.IS_COUNT S.STOCK_NAME
DESC ASC
) RN , S.STOCK_NO, S.STOCK_IMG, S.STOCK_NAME , C.STOCK_CT_NAME , I.IS_COUNT FROM STOCK_T S INNER JOIN STOCK_CATEGORY_T C ON S.STOCK_CT_NO = C.STOCK_CT_NO INNER JOIN INVENTORY_STAT_T I ON S.STOCK_NO = I.STOCK_NO WHERE 1=1 AND I.FRN_NO = #{frnNo}
AND S.STOCK_CT_NO = #{stockCtNo}
AND S.STOCK_NAME LIKE '%' || #{stockNameSearch} || '%'

SELECT C.STOCK_CT_NO, C.STOCK_CT_NAME , S.STOCK_NO, S.STOCK_NAME, S.STOCK_PRICE, S.STOCK_UNIT FROM STOCK_T S INNER JOIN STOCK_CATEGORY_T C ON S.STOCK_CT_NO = C.STOCK_CT_NO WHERE 1=1 AND STOCK_NO IN
#{stockNo}
ORDER BY S.STOCK_NAME ASC

mapper.insert:
INSERT INTO STOCK_ORDER_TEMP_T ( ORDER_TEMP_NO , STOCK_NO , STOCK_ORDER_COUNT , STOCK_ORDER_COST , EMP_NO ) SELECT STOCK_ORDER_TEMP_SEQ.NEXTVAL AS ORDER_TEMP_NO , TEMP_T.* FROM (
SELECT #{stockDto.stockNo} AS STOCK_NO , #{stockDto.stockOrderCount} AS STOCK_ORDER_COUNT , #{stockDto.stockOrderCost} AS STOCK_ORDER_COST , #{empNo} AS EMP_NO FROM DUAL
) TEMP_T


SELECT APPROVAL_SEQ.NEXTVAL
  FROM DUAL

INSERT INTO APPROVAL_T (
            APPR_NO
          , EMP_NO
          , CREATOR
   ) VALUES (
            #{apprNo}
          , #{empNo}
          , #{empNo})

INSERT INTO ORDER_T (
            APPR_NO
          , ORDER_COST
   ) VALUES (
            #{apprNo}
          , #{orderCost})

INSERT INTO STOCK_ORDER_T ( APPR_NO , STOCK_NO , STOCK_ORDER_COUNT , STOCK_ORDER_COST ) SELECT #{apprNo} AS APPR_NO , TEMP_T.* FROM (
SELECT #{stockDto.stockNo} AS STOCK_NO , #{stockDto.stockOrderCount} AS STOCK_ORDER_COUNT , #{stockDto.stockOrderCost} AS STOCK_ORDER_COST FROM DUAL
) TEMP_T

mapper.select:
SELECT COUNT(*) FROM APPROVAL_T A INNER JOIN ORDER_T O ON A.APPR_NO = O.APPR_NO INNER JOIN EMPLOYEE_T E ON A.EMP_NO = E.EMP_NO INNER JOIN FRANCHISE_T F ON E.FRN_NO = F.FRN_NO WHERE 1=1
AND F.FRN_NO = #{frnNo}
AND A.APPR_STAT = #{apprStat}
AND ( F.FRN_NAME LIKE '%' || #{searchKeyword} || '%' OR E.EMP_NAME LIKE '%' || #{searchKeyword} || '%' )
